import React, { useState } from 'react';
import { createPersonnel, updatePersonnel } from '../api'; // İmport ifadesini kontrol edin

const PersonnelForm = ({ selectedPersonnel, onSave }) => {
    const [personnel, setPersonnel] = useState(selectedPersonnel || { name: '', email: '' });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setPersonnel(prevPersonnel => ({ ...prevPersonnel, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (personnel.id) {
            updatePersonnel(personnel.id, personnel)
                .then(response => {
                    console.log('Update response:', response);
                    onSave();
                })
                .catch(error => {
                    console.error('Update error:', error);
                });
        } else {
            createPersonnel(personnel)
                .then(response => {
                    console.log('Create response:', response);
                    onSave();
                })
                .catch(error => {
                    console.error('Create error:', error);
                });
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                name="name"
                value={personnel.name}
                onChange={handleChange}
                placeholder="Name"
            />
            <input
                type="email"
                name="email"
                value={personnel.email}
                onChange={handleChange}
                placeholder="Email"
            />
            <button type="submit">Save</button>
        </form>
    );
};

export default PersonnelForm;
