import axios from 'axios';

const personnelApi = axios.create({
    baseURL: 'http://localhost:8081/api',
});

export const getAllPersonnel = () => personnelApi.get('/personnel');
export const getPersonnelById = (id) => personnelApi.get(`/personnel/${id}`);
export const createPersonnel = (personnel) => personnelApi.post('/personnel', personnel);
export const updatePersonnel = (id, personnel) => personnelApi.put(`/personnel/${id}`, personnel);
export const deletePersonnel = (id) => personnelApi.delete(`/personnel/${id}`);

export default personnelApi;
