import React, { useState } from 'react';
import { createPersonnel, updatePersonnel } from '../api';

const PersonnelForm = ({ existingPersonnel, onSave }) => {
    const [personnel, setPersonnel] = useState(existingPersonnel || { name: '', email: '' });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setPersonnel({ ...personnel, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (personnel.id) {
            await updatePersonnel(personnel.id, personnel);
        } else {
            await createPersonnel(personnel);
        }
        onSave();
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>Name:</label>
                <input type="text" name="name" value={personnel.name} onChange={handleChange} required />
            </div>
            <div>
                <label>Email:</label>
                <input type="email" name="email" value={personnel.email} onChange={handleChange} required />
            </div>
            <button type="submit">Save</button>
        </form>
    );
};

export default PersonnelForm;
